(()=>{let t=[{id:1,title:"Кофе",img:"https://i.pinimg.com/originals/b9/8c/dd/b98cdd89a78aac10701c5ec1ab0f75ae.jpg"},{id:2,title:"Чай",img:"https://trikky.ru/wp-content/blogs.dir/1/files/2020/06/26/s1201.jpg"},{id:3,title:"Десерты",img:"https://almette.ru/uploads/recipe/Qhf7TvJUy1AEC6cq8TGg25jyxtRvFNrt0nprfF3m.jpeg"}];const e=t=>`\n    <div class="col">\n      <div class="card">\n        <img class="card-img-top" style="height: 300px;" src="${t.img}" alt="${t.title}">\n        <div class="card-body">\n          <h5 class="card-title">${t.title}</h5>\n          <a href="#" class="btn btn-primary" data-btn="price" data-id="${t.id}">Открыть</a>\n          <a href="#" class="btn btn-danger" data-btn="remove" data-id="${t.id}">Закрыть</a>\n        </div>\n      </div>\n    </div>\n  `;function i(){const i=t.map(e).join("");document.querySelector("#fruits").innerHTML=i}i();const n=$.modal({closable:!0,width:"400px",footerButtons:[{text:"Закрыть",type:"primary",handler(){n.close()}}]});document.addEventListener("click",(e=>{e.preventDefault();const a=e.target.dataset.btn,c=+e.target.dataset.id,d=t.find((t=>t.id===c));"price"===a?(n.setContent(`\n        <p>Вы открыли ${d.title}: <strong>тут должно быть api</strong></p>\n      `),n.open()):"remove"===a&&$.confirm({title:"Вы уверены?",content:`<p>Вы закрываете: <strong>${d.title}</strong></p>`}).then((()=>{t=t.filter((t=>t.id!==c)),i()})).catch((()=>{console.log("Cancel")}))}))})();